void cITEM::Init()
{
	MatItemInfoBox = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);

	MatItemInfoBox_TopLeft = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxTopLeft.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);
	MatItemInfoBox_TopRight = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxTopRight.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);
	MatItemInfoBox_TopCenter = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxTopCenter.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);

	MatItemInfoBox_BottomLeft = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxBottomLeft.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);
	MatItemInfoBox_BottomRight = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxBottomRight.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);
	MatItemInfoBox_BottomCenter = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxBottomCenter.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);

	MatItemInfoBox_Left = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxLeft.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);
	MatItemInfoBox_Right = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxRight.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);
	MatItemInfoBox_Center = CreateTextureMaterial("Image\\SinImage\\Items\\ItemInfoBox\\BoxCenter.tga", 0, 0, 0, SMMAT_BLEND_ALPHA);

	Load();

	int CheckCount = 0;
	while (1)
	{
		if (NotSell_Item_CODE[NotSell_Item_CODECnt])
			NotSell_Item_CODECnt++;
		else
			CheckCount++;
		if (NotSell_Item_MASK[NotSell_Item_MASKCnt])
			NotSell_Item_MASKCnt++;
		else
			CheckCount++;
		if (NotSell_Item_KIND[NotSell_Item_KINDCnt])
			NotSell_Item_KINDCnt++;
		else
			CheckCount++;

		if (NotDrow_Item_CODE[NotDrow_Item_CODECnt])
			NotDrow_Item_CODECnt++;
		else
			CheckCount++;
		if (NotDrow_Item_MASK[NotDrow_Item_MASKCnt])
			NotDrow_Item_MASKCnt++;
		else
			CheckCount++;
		if (NotDrow_Item_KIND[NotDrow_Item_KINDCnt])
			NotDrow_Item_KINDCnt++;
		else
			CheckCount++;

		if (NotSet_Item_CODE[NotSet_Item_CODECnt])
			NotSet_Item_CODECnt++;
		else
			CheckCount++;
		if (NotSet_Item_MASK[NotSet_Item_MASKCnt])
			NotSet_Item_MASKCnt++;
		else
			CheckCount++;
		if (NotSet_Item_KIND[NotSet_Item_KINDCnt])
			NotSet_Item_KINDCnt++;
		else
			CheckCount++;

		if (CheckCount >= 9)
			break;

	}
	memset(g_Manufacture_ItemInfo, 0, sizeof(SManufacture_ResultItemInfo));
}
