int cITEM::GetInfoBoxSize(ItemData *pItem, int Col)
{
	int ty, tx;

	ItemBoxSize.x = 10;
	ItemBoxSize.y = Col + 1;

	ItemBoxPosi.x = (pItem->iPlaceX + (pItem->iWidth / 2)) - ((ItemBoxSize.x * 16) / 2);
	ItemBoxPosi.y = pItem->iPlaceY - (ItemBoxSize.y * 16);


	ty = ItemBoxPosi.y + (ItemBoxSize.y * 16);
	tx = ItemBoxPosi.x + (ItemBoxSize.x * 16);

	if (ItemBoxPosi.y < 0)
		ItemBoxPosi.y = 0;
	if (ItemBoxPosi.x < 0)
		ItemBoxPosi.x = 0;

	if (ty > 600)
		ItemBoxPosi.y -= (ty - 600);
	if (tx > 800)
		ItemBoxPosi.x -= (tx - 800);

	return TRUE;
}

